function Item(props) {
    const image = require.context('../images', true);

    return <div>
        <div>
            <img src={image(`./${props.image}`)} alt='item image' />
        </div>
        <div>
            <h3>{props.name}</h3>
        </div>
    </div>
};

export default Item;
